package project_euler;

class q08 {
    public static void main(String args[]) {
        long arr[] = new long[1000];
        // stores the number which we want to convert to int array
        String convN = "";
        // get the chars and store them in the int array
        for(int x = 0; x != 1000; x++) {
            switch(convN.charAt(x)) {
                case '0':
                    arr[x] = 0;
                    break;
                case '1':
                    arr[x] = 1;
                    break;
                case '2':
                    arr[x] = 2;
                    break;
                case '3':
                    arr[x] = 3;
                    break;
                case '4':
                    arr[x] = 4;
                    break;
                case '5':
                    arr[x] = 5;
                    break;
                case '6':
                    arr[x] = 6;
                    break;
                case '7':
                    arr[x] = 7;
                    break;
                case '8':
                    arr[x] = 8;
                    break;
                case '9':
                    arr[x] = 9;
                    break;
            }
        }

        // find the greatest product of 4 adjacent digits
        long x , y, greatest = 0;
        for(x = 0, y = 12; y != 1000; y++) {
            long product = 1;
            for(long z = x; z <= y; z++)
                product *= arr[(int) z];
            if(product > greatest) {
                greatest = product;
            }
            ++x;
        }
        System.out.println("Ans: " + greatest);
    }
}